import React, { Component } from 'react';
import axios from 'axios';

const url='api.openweathermap.org/data/2.5/forecast';
const api='071b80c778e5017e5d6c14ce497cf874';
const city='cali,col';
export default class Items extends Component{
state = {
    persons: []
    }

    componentDidMount() {
        const urlapi =`${url}?q=${city}&appid=${api}`;
    axios.get('api.openweathermap.org/data/2.5/forecast?q=cali,col&appid=071b80c778e5017e5d6c14ce497cf874')
        .then(res => {
        const persons = res.data;
        console.log(persons);
        this.setState({ persons });
        })
    }

    render() {
        return (
            <ul>
            { this.state.persons.map(person => <li>{person.name}</li>)}
            </ul>
        )
    }
}
