import React, { Component } from 'react'
import axios from 'axios';
import {
    Link
} from 'react-router-dom'

class Nav extends Component{
    state = {
        categorias: []
      }
     
      componentWillMount() {
    
        axios.get(`https://localhost/alain/cotizador/api/controller/getCategorias.php`).then((response) => {
            this.setState({
              categorias:response.data
            })
          });
      }
    
    render(){
        let categorias=this.state.categorias.map((categoria)=>{
            return(
                <li>
                    <Link to="/">categoria.name</Link>
                </li>
            )
          
        });
        console.log(categorias);
        return(
                <div>
                    <ul>
                        ${categorias}
                    </ul>
                </div>
        );
    }

}
export default Nav;
