import React, { Component } from 'react'
import axios from 'axios';
import {
    Link
} from 'react-router-dom'

class Nav extends Component{
    state = {
        categorias: []
      }
     
      componentWillMount() {
        fetch('http://localhost/alain/cotizador/public/api/controller/getCategorias.php').then(data =>{ 
            console.log(data)
            data.json()
        }).then(categorias =>{
            console.log(categorias)
            const data=this.getData(categorias)
            this.setState({categorias: data})
        });
       
      }
    
    render(){
        let categorias=this.state.categorias.map((categoria)=>{
            return(
                <li>
                    <Link to="/">${categoria.nombre}</Link>
                </li>
            )
          
        });
        return(
                <div>
                    <ul>
                        ${categorias}
                    </ul>
                </div>
        );
    }

}
export default Nav;
